INSERT INTO "public"."content" VALUES ('384', 'http://github.com/github/site-policy', 'Collaborative development on GitHub''s site policies, procedures, and guidelines - GitHub - github/site-policy: Collaborative development on GitHub''s site policies, procedures, and guidelines 
        Collaborative development on GitHub''s site policies, procedures, and guidelines
       
        Use Git or checkout with SVN using the web URL.
     
